const mapValueToColor = ({ value }) => {
  if (value === -1) {
    return '#ccc';
  } else if (value === -2) {
    return '#aaa';
  } else if (value < 50) {
    return '#8cc665';
  } else if (value < 100) {
    return '#ffff00';
  } else if (value < 150) {
    return '#ffa340';
  }
  return '#ff0000';
};

