import React from 'react';
import { func, number, string, arrayOf, any } from 'prop-types';
import getRelativeCoordinates from './getRelativeCoordinates';

function drawHeatmapLine({
  node,
  data = [],
  text = '2017',
  textAlign = 'left',
  itemWidth = 5,
  itemHeight = 20,
  legendWidth = 5,
  font,
  mapValueToColor,
}) {
  const ctx = node.getContext('2d');
  const sourceWidth = node.clientWidth;
  const itemsWidth = data.length * itemWidth;
  const scaleFactor = sourceWidth * (1 - legendWidth / 100) / itemsWidth;
  const scaledLegendWidth = Math.floor(
    sourceWidth * legendWidth / (100 * scaleFactor),
  );
  node.width = itemsWidth + scaledLegendWidth;
  node.height = itemHeight;
  const offsetLeft = textAlign === 'left' ? scaledLegendWidth : 0;
  ctx.font = font;
  ctx.fillStyle = 'black';
  ctx.textBaseline = 'middle';
  ctx.textAlign = 'center';
  ctx.fillText(
    text,
    (offsetLeft ? 0 : itemsWidth) + scaledLegendWidth / 2,
    node.height / 2,
    scaledLegendWidth * 0.75,
  );
  data.forEach((element, idx) => {
    ctx.fillStyle = mapValueToColor(element);
    ctx.fillRect(
      offsetLeft + itemWidth * idx + 1,
      0,
      itemWidth - 1,
      itemHeight,
    );
  });
}

class HeatmapRow extends React.PureComponent {
  /* eslint-disable react/no-unused-prop-types */
  static propTypes = {
    data: arrayOf(any).isRequired,
    text: string,
    textAlign: string,
    font: string,
    itemWidth: number,
    itemHeight: number,
    legendWidth: number,
    mapValueToColor: func,
    onSelectEntry: func,
    onMouseLeave: func,
  };

  static defaultProps = {
    text: '',
    textAlign: 'right',
    font: '20px Arial',
    itemWidth: 5,
    itemHeight: 20,
    legendWidth: 5,
    mapValueToColor: v => v,
    onSelectEntry: () => {},
    onMouseLeave: () => {},
  };

  componentDidMount() {
    this.drawCanvas();
  }

  componentDidUpdate() {
    this.drawCanvas();
  }

  drawCanvas() {
    drawHeatmapLine({
      node: this.node,

      ...this.props,
    });
  }

  handleMove = e => {
    const coords = getRelativeCoordinates(e, this.node);
    const width = this.node.clientWidth;
    const percent = coords.x / width * 100;
    const dataWidth = 100 - this.props.legendWidth;
    if (this.props.textAlign === 'left' && percent < this.props.legendWidth) {
      return this.props.onSelectEntry(null);
    }
    if (this.props.textAlign === 'right' && percent > dataWidth) {
      return this.props.onSelectEntry(null);
    }

    const relativePercent = percent / dataWidth;
    return this.props.onSelectEntry(
      this.props.data[Math.round(this.props.data.length * relativePercent)],
    );
  };

  render() {
    return (
      <canvas
        onMouseMove={this.handleMove}
        onMouseLeave={this.props.onMouseLeave}
        ref={node => {
          this.node = node;
        }}
      />
    );
  }
}

export default HeatmapRow;

export default function getRelativeCoordinates(e, container) {
  const pos = {};
  const offset = {};
  let ref;

  ref = container.offsetParent;
  pos.x = e.touches ? e.touches[0].pageX : e.pageX;
  pos.y = e.touches ? e.touches[0].pageY : e.pageY;

  offset.left = container.offsetLeft;
  offset.top = container.offsetTop;

  while (ref) {
    offset.left += ref.offsetLeft;
    offset.top += ref.offsetTop;

    ref = ref.offsetParent;
  }

  return {
    x: pos.x - offset.left,
    y: pos.y - offset.top,
  };
}

import React from 'react';
import { func, arrayOf, any, string } from 'prop-types';
import HeatmapRow from './HeatmapRow';
import getRelativeCoordinates from './getRelativeCoordinates';

function prepareData({ startDate, endDate, values, extractDate }) {
  if (endDate < startDate) {
    throw new Error('End date should be greater than start date');
  }
  const cache = Object.create(null);
  values.forEach(entry => {
    const date = extractDate(entry);
    const day = date.toISOString().substr(0, 10);
    if (cache[day]) {
      throw new Error(`Duplicate record for ${day}`);
    }
    cache[day] = entry;
  });

  const normalizedDataSplits = Object.create(null);
  const startYear = new Date(startDate).getFullYear();
  const endYear = new Date(endDate).getFullYear();
  for (let year = startYear; year <= endYear; year += 1) {
    normalizedDataSplits[year] = [];
    for (
      let currentDate = new Date(year, 0, 1);
      currentDate.getFullYear() === year;
      currentDate.setDate(currentDate.getDate() + 1)
    ) {
      if (currentDate.getMonth() === 1 && currentDate.getDate() === 29) {
        continue; // eslint-disable-line no-continue
      }
      const day = currentDate.toISOString().substring(0, 10);
      normalizedDataSplits[year].push(
        cache[day] || {
          date: currentDate.toISOString(),
          value: -1,
        },
      );
    }
  }

  return normalizedDataSplits;
}

export default class Heatmap extends React.Component {
  static propTypes = {
    // eslint-disable-next-line react/forbid-prop-types
    values: arrayOf(any).isRequired,
    startDate: string.isRequired,
    endDate: string.isRequired,
    mapValueToColor: func.isRequired,
    renderTooltip: func,
  };

  static defaultProps = {
    renderTooltip: () => null,
  };

  constructor(props) {
    super();

    this.state = { data: prepareData(props) };
  }

  componentWillReceiveProps(newProps) {
    if (
      newProps.values !== this.props.values ||
      newProps.startDate !== this.props.startDate ||
      newProps.endDate !== this.props.endDate
    ) {
      this.setState({ data: prepareData(newProps), tooltipEntry: null });
    }
  }

  clearCoords = () => {
    this.setState({ tooltipEntry: null });
  };

  handleMouseMove = e => {
    this.setState({
      tooltipCoords: getRelativeCoordinates(e, this.container),
    });
  };

  selectEntry = e => {
    this.setState({ tooltipEntry: e });
  };

  render() {
    const { data, tooltipEntry, tooltipCoords } = this.state;
    const { mapValueToColor, renderTooltip } = this.props;
    return (
      <div
        style={{ position: 'relative' }}
        ref={c => {
          this.container = c;
        }}
      >
        <div onMouseMove={this.handleMouseMove} onMouseLeave={this.clearCoords}>
          {Object.keys(data).map(year => (
            <HeatmapRow
              key={year}
              onSelectEntry={this.selectEntry}
              onMouseLeave={this.clearCoords}
              text={year}
              data={data[year]}
              mapValueToColor={mapValueToColor}
            />
          ))}
        </div>
        {tooltipEntry && (
          <div
            style={{
              position: 'absolute',
              zIndex: 2,
              left: tooltipCoords.x,
              top: tooltipCoords.y,
              transform: 'translateX(-50%) translateY(5px)',
            }}
            className="foo"
          >
            {renderTooltip(tooltipEntry)}
          </div>
        )}
      </div>
    );
  }
}

import moment from 'moment';
import AirQualityRecord from './models/AirQualityRecord';
import ExposomicsLocationManager from '../../../utils/ExposomicsLocationManager';
import ExposomicsData from '../../../utils/ExposomicsData';
// const AqiExposomics = require('./ExposomicsManager')

/**
 * This function accepts a list of places (json) and returns data to be rendered by the React component

 * @param {Array} places A json object of location and time data from the Google Api

 * @return {Object}
 * @property {moment} result.startDate
 * @property {moment} result.endDate
 * @property {Array} result.dataList
 */
export default (async function controller(places) {
  // Create the location manager with the list of places.
  const locationManager = new ExposomicsLocationManager(places);

  /**
   * queryLogicFunction is a logic function that will be passed to ExposomicsData to construct the list of data points

   * @param {moment} date
   */
  const queryLogicFunction = async function queryLogic(date) {
    // This function has the logic that takes a date, gets the location, queries the database, and returns the AQI
    // Note - this function has access to collection and locationManager from the closure.

    // First get the location corresponding to the input date
    const location = locationManager.getLocation(date);
    if (!location) return -1;

    // Use that location to prepare the database query
    const state = location.getState();
    const dateString = date.format('YYYY-MM-DD');
    let county = location.getCounty();
    county = county.replace(' County', '');

    // Make the database query
    let result;
    try {
      // Make the async call to the database to get the AQI for a particular location and date.
      // If a different database structure is used, this line will have to get modified (but only this line)
      result = await AirQualityRecord.findOne({
        state,
        county,
        date: dateString,
      });
    } catch (e) {
      console.log(e); // eslint-disable-line no-console
    }

    // Return the result
    if (!result) return -2;
    return result.AQI; // Return the actual result to be rendered (also depends on database structure).
  };

  // Set up the dates and date ranges
  const startDate = locationManager.getStartDate();
  const endDate = locationManager.getEndDate();
  const interval = moment.duration(1, 'days');

  // Construct an ExposomicsData object with queryLogicFunction and the date information
  // The ExposomicsData constructor will iterate over the specified date range and query the logic function to get results
  //   for each date.
  const dataObject = new ExposomicsData(
    queryLogicFunction,
    startDate,
    endDate,
    interval,
  );

  await dataObject.populateDataList(); // This needs to be a seperate call because it's an async function
  // and can't be done in the constructor of ExposomicsData

  // Return results in the form that the React component is expecting.
  const result = {
    dataList: dataObject.dataList,
    startDate,
    endDate,
  };
  return result;
});

import mongoose from 'mongoose';

const AirQualityRecordSchema = new mongoose.Schema(
